CREATE TABLE Player (id INT AUTO_INCREMENT, firstName VARCHAR(20) NOT NULL, lastName VARCHAR(20) NOT NULL, handle VARCHAR(20), dob DATE NOT NULL, country VARCHAR(20) NOT NULL, gender ENUM('M','F') NOT NULL, PRIMARY KEY(id));
CREATE TABLE Team (id INT AUTO_INCREMENT, teamName VARCHAR(20) NOT NULL, dateFormed DATE NOT NULL, dateDisbanded DATE, PRIMARY KEY(id));
CREATE TABLE TeamMember (teamID INT, memberID INT, PRIMARY KEY(teamID,memberID), FOREIGN KEY(teamID) REFERENCES Team(id), FOREIGN KEY(memberID) REFERENCES Player(id));
CREATE TABLE Event (name VARCHAR(20), prize INT NOT NULL, city VARCHAR(20) NOT NULL, state VARCHAR(2) NOT NULL, winningTeamID INT, PRIMARY KEY(name), FOREIGN KEY(winningTeamID) REFERENCES Team(id));
CREATE TABLE MatchScore (id INT AUTO_INCREMENT, teamA INT, teamB INT, teamAWins INT(5) NOT NULL, teamBWins INT(5) NOT NULL, event VARCHAR(20), PRIMARY KEY(id), FOREIGN KEY(teamA) REFERENCES Team(id), FOREIGN KEY(teamB) REFERENCES Team(id), FOREIGN KEY(event) REFERENCES Event(name));